components.custom:
var ownerId = Math.random().toString();
var currentRecord = Citeck.Records.get(instance.options.recordId);

var configFormRef = data.configFormRef;
if (Array.isArray(configFormRef)) {
    configFormRef = configFormRef[0];
}

if (!configFormRef && data.parent) {
    configFormRef = Citeck.Records.get(data.parent).load('inhConfigFormRef?id', true);
}

Promise.all([configFormRef, currentRecord.load('config?json')])
    .then(function(formAndConfig) {

        var formRef = formAndConfig[0];
        var config = formAndConfig[1];

        if (formRef == null) {
            throw new Error("Config form is not set");
        }

        if (!config) {
            config = {};
        }

        var configRecord = Citeck.Records.create(config, ownerId);

        Citeck.forms.eform(configRecord.id, {
            params: {
                formId: formRef,
                onSubmit: function (record) {
                    var data = record.toJson().attributes;
                    var resData = {};
                    for (var key in data) {
                        if (key[0] !== '_' && key !== 'pendingUpdate') {
                            resData[key] = data[key];
                        }
                    }
                    currentRecord.att("config?json", resData);
                    Citeck.Records.release(configRecord.id, ownerId);
                },
                onCancel: function () {
                    Citeck.Records.release(configRecord.id, ownerId);
                },
                saveOnSubmit: false
            },
            class: 'ecos-modal_width-lg',
            isBigHeader: true
        });
    })
